import React, { Component } from 'react'
import axios from 'axios'
class PostForm extends Component {

    constructor(props) {
      super(props)
    
      this.state = {
         userId:'',
         password:''
      }
    }

    changeHandler = e =>{
        this.setState({[e.target.name]:e.target.value})
    }    

    submitHandler = e=>{
        e.preventDefault()
        console.log(this.state)
        const stateJson = JSON.stringify(this.state);
        console.log(typeof(stateJson))
        axios.post('http://127.0.0.1:5000/signup',JSON.parse(stateJson)).then(response=>{
            console.log(response)
        }).catch(error=>{
            console.log(error)
        })
    }

    // submitHandler = e=>{
    //     e.preventDefault()
    //     console.log(this.state)
    //     return fetch('http://127.0.0.1:8080/signup',)
    // }

  render() {
    const {userId, password}=this.state
    return (
      <div>
        <form onSubmit={this.submitHandler} action='POSTS'>
            <div>
                <input type='text' name='userId' value={userId} onChange={this.changeHandler} />
            </div>
            <div>
                <input type='text' name='password' value={password} onChange={this.changeHandler} />
            </div>
            <div>
                <button type='submit'>submit</button>
            </div>
        </form>
      </div>
    )
  }
}

export default PostForm
