import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ViewprojectEmployee(props) {
    const [EmpId] = useState(props.userId);
    const [projectData, setProjectData] = useState(null);

    useEffect(() => {
        const getTable = () => {
            axios.post('http://127.0.0.1:5000/getProjectForEmployee', { EmpId })
                .then((response) => {
                    console.log(response.data)
                    setProjectData(JSON.parse(response.data));
                })
                .catch((error) => {
                    console.error(error);
                });
        }
        getTable();
    }, [EmpId]);

    const handleEdit = (row) => {
        console.log("Editing project with ID:", row.project_id);
    };

    return (
        <div>
            returns list of projects that employee is having for {EmpId};
            {projectData && (
                <table>
                    <thead>
                        <tr>
                            <th>Project_id</th>
                            <th>Project_leader</th>
                            <th>Client_id</th>
                            <th>Project_name</th>
                            <th>Project_log</th>
                            <th>domain</th>
                            <th>Members</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {projectData.map((row, index) => (
                            <tr key={index}>
                                <td>{row.project_id}</td>
                                <td>{row.project_leader}</td>
                                <td>{row.client_id}</td>
                                <td>{row.project_name}</td>
                                <td>{row.project_log}</td>
                                <td>{row.domain}</td>
                                <td>{row.members}</td>
                                <td>
                                    <button onClick={() => handleEdit(row)}>edit</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    )
}

export default ViewprojectEmployee;
