import axios from 'axios';
import React, { useState, useEffect } from 'react'; // Added useEffect and useState

function CreateForm() {
    
    const [EmployeeList, setEmployeeData] = useState([]);

    const submitHandler = () => {
        console.log('hi');
    }

    const getEmployeeList = () => {
        axios.post('http://127.0.0.1:5000/getEmployeeListDropDown')
            .then((response) => {
                console.log("API Response:", response.data); // log the direct response data
                setEmployeeData(JSON.parse(response.data));  // direct assignment
            })
            .catch((error) => {
                console.log(error);
            })
    }

    useEffect(() => { 
        getEmployeeList(); 
    },[]);

    useEffect(() => { 
        console.log("Updated Employee List:", EmployeeList); // This will show the updated EmployeeList whenever it changes
    },[EmployeeList]);

    return (
      <div>
        <form onSubmit={submitHandler}>
        <div>
            <p>Project_id</p>
            <input type='text' name='Project_id'></input>
        </div>

        <div>
            <p>Client_id</p>
            <input type='text' name='Client_id'></input>
        </div>

        <div>
            <p>Project_name</p>
            <input type='text' name='Project_name'></input>
        </div>

        <div>
            <p>Project_log</p>
            <input type='text' name='Project_log' placeholder='ussually requested'></input>
        </div>

        <div>
            <p>domain</p>
            <input type='text' name='domain'></input>
        </div>
  
        <div>
              <p>project_leader</p>
              <select name="project_leader">
                  {EmployeeList && EmployeeList.map(employee => (
                      <option key={employee} value={employee}>
                          {employee} 
                      </option>
                  ))}
              </select>
          </div>
  
          <div>
              <button type='submit'>submit</button>
          </div>
        </form>
      </div>
    )
}

export default CreateForm;
