  const getProjId = () => {
    axios.post('http://127.0.0.1:5000/getNewProjectId').then((response) => {
      console.log(response.data);
      setProjectId(response.data);
    });
  }

  useEffect(() => { 
    getProjId(); 
  }, []);


  const getEmployeeList = () => {
