import axios from 'axios';
import React, { useState, useEffect } from 'react'; // Added useEffect and useState

function CreateForm() {
    
    const [EmployeeList, setEmployeeData] = useState([]);

    const submitHandler = () => {
        console.log('hi');
    }

    const getEmployeeList = () => {
        axios.post('http://127.0.0.1:5000/getEmployeeListDropDown')
            .then((response) => {
                console.log(response);
                setEmployeeData(response.data); // Assuming axios returns the parsed object
            })
            .catch((error) => {
                console.log(error);
            })
    }

    useEffect(() => { 
        getEmployeeList(); 
    }, []);  // Ensures the function is called once when the component mounts

    return (
      <div>
        <form onSubmit={submitHandler}>
          {/* ... your other input fields ... */}
  
          <div>
              <p>project_leader</p>
              <select name="project_leader">
                  {EmployeeList && EmployeeList.map(employee => (
                      <option key={employee.id} value={employee.id}>
                          {employee.name}  
                      </option>
                  ))}
              </select>
          </div>
  
          <div>
              <button type='submit'>submit</button>
          </div>
        </form>
      </div>
    )
}

export default CreateForm;
