import React, { Component } from 'react';
import axios from 'axios';
import Client from './Client';
import Admin from './Admin';
import Employee from './Employee';

class PostForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userId: '',
      password: '',
      userRole: '', // Added userRole state to store the role of the user
    };
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
    const stateJson = JSON.stringify(this.state);
    axios
      .post('http://127.0.0.1:5000/signin', JSON.parse(stateJson))
      .then((response) => {
        console.log(response.data);
        if (response.data === 'True') {
          const userId = this.state.userId;
          if (userId.charAt(0) === 'C') 
          {
            this.setState({ userRole: 'client' }); 
          } 
          else if (userId.charAt(0) === 'A') 
          {
            this.setState({ userRole: 'admin' });
          } 
          else if (userId.charAt(0) === 'E') 
          {
            this.setState({ userRole: 'employee' });
          }
        } 
        else 
        {
          console.log('Enter correct details');
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { userId, password, userRole } = this.state;
    if (userRole === 'client') {
      return <Client />;
    } else if (userRole === 'admin') {
      return <Admin />;
    } else if (userRole === 'employee') {
      return <Employee />;
    }
    return (
      <div>
        <form onSubmit={this.submitHandler} action='POSTS'>
          <div>
            <input type='text' name='userId' value={userId} onChange={this.changeHandler} />
          </div>
          <div>
            <input type='text' name='password' value={password} onChange={this.changeHandler} />
          </div>
          <div>
            <button type='submit'>Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

export default PostForm;
