import axios from 'axios';
import React, { useState, useEffect } from 'react';

function CreateForm() {
    
    const [EmployeeList, setEmployeeData] = useState([]);

    const [projectId, setProjectId] = useState('');
    const [clientId, setClientId] = useState('');
    const [projectName, setProjectName] = useState('');
    const [projectLog, setProjectLog] = useState('');
    const [domain, setDomain] = useState('');
    const [projectLeader, setProjectLeader] = useState('');

    const submitHandler = (e) => {
        e.preventDefault();  // Prevents page refresh on form submission
        const formData = {
            projectId,
            clientId,
            projectName,
            projectLog,
            domain,
            projectLeader
        };
        console.log("Form Data:", formData);
    }

    const getProjId=()=>{
      axios.post('http://127.0.0.1:5000/getNewProjectId').then((response)=>{
        console.log(response.data);
        setProjectId(response.data);
      })
    }

    const getEmployeeList = () => {
        axios.post('http://127.0.0.1:5000/getEmployeeListDropDown')
            .then((response) => {
                console.log("API Response:", response.data);
                setEmployeeData(JSON.parse(response.data));
            })
            .catch((error) => {
                console.log(error);
            });
    }

    useEffect(() => { 
        getEmployeeList(); 
    }, []);

    useEffect(() => { 
        console.log("Updated Employee List:", EmployeeList);
    }, [EmployeeList]);

    return (
      <div>
        <p>{getProjId}</p>
        <form onSubmit={submitHandler}>
          <div>
            <p>Project_id</p>
            <input type='text' readOnly name='Project_id' value={projectId} onChange={(e) => setProjectId(e.target.value) } />
          </div>

          <div>
            <p>Client_id</p>
            <input type='text' name='Client_id' value={clientId} onChange={(e) => setClientId(e.target.value)} />
          </div>

          <div>
            <p>Project_name</p>
            <input type='text' name='Project_name' value={projectName} onChange={(e) => setProjectName(e.target.value)} />
          </div>

          <div>
            <p>Project_log</p>
            <input type='text' name='Project_log' value={projectLog} placeholder='usually requested' onChange={(e) => setProjectLog(e.target.value)} />
          </div>

          <div>
            <p>domain</p>
            <input type='text' name='domain' value={domain} onChange={(e) => setDomain(e.target.value)} />
          </div>

          <div>
            <p>project_leader</p>
            <select name="project_leader" value={projectLeader} onChange={(e) => setProjectLeader(e.target.value)}>
                {EmployeeList && EmployeeList.map(employee => (
                    <option key={employee} value={employee}>
                        {employee} 
                    </option>
                ))}
            </select>
          </div>

          <div>
            <button type='submit'>submit</button>
          </div>
        </form>
      </div>
    );
}

export default CreateForm;
