import React, { Component } from 'react';
import PostForm from './PostForm';
import ListProjects from './ListProjects';
import FormProject from './FormProject';
import axios from 'axios';

class Client extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
      loggedIn: true,
      userId:props.userId,
      name:props.name,
    }
  }

  logouter = (e) => {
    this.setState({ loggedIn: false });
  };


 getTable = (e) => {
  console.log('hi');
  const stateJson=JSON.stringify(this.state);
  console.log('before sending'+stateJson);
  axios.post('http://127.0.0.1:5000/getProject', JSON.parse(stateJson))
    .then((response) => {
      console.log(response);
    })
    .catch((error) => {
      console.error(error);
    });
  }

  getForm=(e)=>{
    console.log('hi');
  }

  render() {
    const { loggedIn, userId ,name} = this.state;

    return (
      <div>
        {loggedIn ? (
          <div>
            <h1>Welcome {userId} {name}</h1>
            <button onClick={this.getTable}>view projects</button>
            <button onClick={this.getForm}>request projects</button>
            <button onClick={this.logouter}>Logout</button>
          </div>
        ) : (
          <PostForm />
        )}
      </div>
    );
  }
}

export default Client;
