import React, { Component } from 'react';
import axios from 'axios';
import PostForm from './PostForm';
import './SignUpStyles.css'

export class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {
          userName: '',
          company: '',
          password: '',
          email: '',
          signup: 'False',
        }
      };
    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value });
      };
    submitHandler=(e)=>{
        e.preventDefault();
        console.log(this.state);
        const stateJson = JSON.stringify(this.state);
        axios.post('http://127.0.0.1:5000/signup',JSON.parse(stateJson)).then((response)=>{
            console.log(response.data);

        }).catch((error)=>{
            console.log(error);
        })
    };
    toPostForm=(e)=>{
        e.preventDefault();
        this.setState({signup:'True'})
    };
  render() {
    const {userName, company, email, password, signup}=this.state;
    if(signup === "True")
    {
        return <PostForm />
    }
    return (
      <div className='body'>
        <form onSubmit={this.submitHandler} action='POSTS' className='frm'>
          <h2>Signup</h2>
          <div>
            <input type='text' name='userName' value={userName} onChange={this.changeHandler} placeholder='Name'/>
          </div>
          <div>
            <input type='text' name='company' value={company} onChange={this.changeHandler} placeholder='Company'/>
          </div>
          <div>
            <input type='text' name='email' value={email} onChange={this.changeHandler} placeholder='Email'/>
          </div>
          <div>
            <input type='password' name='password' value={password} onChange={this.changeHandler} placeholder='Password'/>
          </div>
          <div>
            <button type='submit'>Submit</button>
          </div>
          <div>
            <button onClick={this.toPostForm}>Signin</button>
          </div>
      </form>
      </div>
    )
  }
}

export default Signup
