import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TeamManager(props) {
  const [AdmId] = useState(props.userId);
  const [projectData, setProjectData] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [dropdownCount, setDropdownCount] = useState(1);
  const [editedMembers, setEditedMembers] = useState([]);
  const [EmployeeList, setEmployeeData] = useState([]);
  const [editedProjectLeader, setEditedProjectLeader] = useState("");

  useEffect(() => {
    const getTeamList = () => {
      axios.post('http://127.0.0.1:5000/GetTeamDetails')
        .then((response) => {
          setProjectData(JSON.parse(response.data))
        })
        .catch((error) => {
          console.log(error);
        });
    }
    getTeamList();
  }, [AdmId]);

  const getEmployeeList = () => {
    axios.post('http://127.0.0.1:5000/getEmployeeListDropDown')
      .then((response) => {
        setEmployeeData(JSON.parse(response.data));
      });
  }

  useEffect(() => {
    getEmployeeList();
  }, []);

  const handleEdit = (row) => {
    setSelectedProject(row);
    setEditedMembers(row.members.split(','));
    setDropdownCount(row.members.split(',').length || 1);
    setEditedProjectLeader(row.project_leader);
  };

  const handleTeammateChange = (index, value) => {
    const newTeammates = [...editedMembers];
    newTeammates[index] = value;
    setEditedMembers(newTeammates);
  };

  const handleCommitChanges = () => {
    console.log('Updated Project Leader:', editedProjectLeader);
    console.log('Updated Team Members:', editedMembers);
    // You can further call APIs to commit the changes to backend here
  };

  return (
    <div>
      {projectData && (
        <table>
          <thead>
            <tr>
              <th>Project Id</th>
              <th>Project Leader</th>
              <th>Members</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {projectData.map((row, index) => (
              <tr key={index}>
                <td>{row.project_id}</td>
                <td>{row.project_leader}</td>
                <td>{row.members}</td>
                <td><button onClick={() => handleEdit(row)}>Edit</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {selectedProject && (
        <div>
          <h3>Edit Project details</h3>
          <p>Project id: {selectedProject.project_id}</p>

          <p>Project Leader:</p>
          <select value={editedProjectLeader} onChange={(e) => setEditedProjectLeader(e.target.value)}>
            {EmployeeList.map(employee => (
              <option key={employee} value={employee}>
                {employee}
              </option>
            ))}
          </select>

          {[...Array(dropdownCount)].map((_, index) => (
            <div key={index}>
              <select 
                value={editedMembers[index] || ""} 
                onChange={(e) => handleTeammateChange(index, e.target.value)}
              >
                <option value="" disabled>Select Member</option>
                {EmployeeList.map(employee => (
                  <option key={employee} value={employee}>
                    {employee}
                  </option>
                ))}
              </select>
            </div>
          ))}

          <button type="button" onClick={() => setDropdownCount(prevCount => prevCount + 1)}>Add Member</button>
          {dropdownCount > 1 && <button type="button" onClick={() => setDropdownCount(prevCount => prevCount - 1)}>Remove Member</button>}
          <button onClick={handleCommitChanges}>Commit Changes</button>
        </div>
      )}
    </div>
  );
}

export default TeamManager;
