import axios from 'axios';
import React, { useState, useEffect } from 'react'; // Added useEffect and useState

function CreateForm() {
    
    const [EmployeeList, setEmployeeData] = useState([]);

    const submitHandler = () => {
        console.log('hi');
    }

    const getEmployeeList = () => {
        axios.post('http://127.0.0.1:5000/getEmployeeListDropDown')
            .then((response) => {
                console.log(response);
                setEmployeeData(JSON.parse(response.data)); // Assuming axios returns the parsed object
            })
            .catch((error) => {
                console.log(error);
            })
    }

    useEffect(() => { 
        getEmployeeList(); 
    }, []);  // Ensures the function is called once when the component mounts

    return (
      <div>
        <form onSubmit={submitHandler}>
        <div>
            <p>Project_id</p>
            <input type='text' name='Project_id'></input>
        </div>

        <div>
            <p>Client_id</p>
            <input type='text' name='Client_id'></input>
        </div>

        <div>
            <p>Project_name</p>
            <input type='text' name='Project_name'></input>
        </div>

        <div>
            <p>Project_log</p>
            <input type='text' name='Project_log' placeholder='ussually requested'></input>
        </div>

        <div>
            <p>domain</p>
            <input type='text' name='domain'></input>
        </div>
  
        <div>
              <p>project_leader</p>
              <select name="project_leader">
                  {EmployeeList.map(employee => (
                      <option key={employee.id} value={employee.id}>
                          {employee.name} 
                      </option>
                  ))}
              </select>
          </div>
  
          <div>
              <button type='submit'>submit</button>
          </div>
        </form>
      </div>
    )
}

export default CreateForm;
