import React, { Component } from 'react'
import PostForm from './PostForm';
import View_project_Employee from './View_project_Employee';
import ProjectStatus from './ProjectStatus';

class Employee extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
      loggedIn: true,
      userId:props.userId,
      name:props.name,
      view_project:false,
      project_status:false,
    }
  }

  project_view_handler=(e)=>{
    this.setState({
      view_project:true,
      project_status:false
    })
   }

   project_status_handler=(e)=>{
    this.setState({
      view_project:false,
      project_status:true
    })
   }

  logouter = (e) => {
    this.setState({ loggedIn: false });
  };
  render() {
    const { loggedIn, userId, name, view_project, project_status } = this.state;
    return (
      <div>
        {loggedIn?(
          <div>
            <div>
              <h1>Welcome {userId} {name}</h1>
              <button onClick={this.project_view_handler}>view projects</button>
              <button onClick={this.project_status_handler}>project status</button>
              <button onClick={this.logouter}>Logout</button>
              {view_project && <View_project_Employee userId={userId}/> }
              {project_status && <ProjectStatus userId={userId}/>}
            </div>
          </div>
        ):(<PostForm/>)}
      </div>
    )
  }
}

export default Employee
