import React, { useState, useEffect } from 'react';
import axios from 'axios';


function AdminListEmployee() {

  const [EmployeeList, setEmployeeData] = useState([]);

  const getEmployeeList = () => {
    axios.post('http://127.0.0.1:5000/getEmployeeListToEdit')
      .then((response) => {
        console.log(JSON.parse(response.data));
        setEmployeeData(JSON.parse(response.data));
      });
  }
  useEffect(() => {
    getEmployeeList();
  }, []);

  const handleEdit = (row) => {
    console.log(row)
  };

  return (
    <div>
      this will return list of employees
      {EmployeeList && (
      <table>
        <thead>
          <th>Employee Id</th>
          <th>Name</th>
          <th>Email</th>
          <th>Action</th>
        </thead>
        <tbody>
          {
            EmployeeList.map((row,index)=>(
              <tr key={index}>
                <td>{row.emp_id}</td>
                <td>{row.name}</td>
                <td>{row.email}</td>
                <td><button onClick={handleEdit(row)}></button></td>
              </tr>
            ))
          }
        </tbody>
      </table>)}
    </div>
  )
}

export default AdminListEmployee
