import React, { useState, useEffect } from 'react'
import axios from 'axios';

function TeamManager(props) {
  const [AdmId] = useState(props.userId);
  const [projectData, setProjectData] = useState(null);

  useEffect(()=>{
    const getTeamList=()=>{
        axios.post('http://127.0.0.1:5000/GetTeamDetails').then((response)=>{
        console.log(JSON.parse(response.data));
        setProjectData(JSON.parse(response.data))
        }).catch((error)=>{
          console.log(error);
        },)
      }
      getTeamList();
  }, [AdmId] )
  return (
    <div>
      returns option to edit the Teams
      {projectData && (
        <table>
          <thead>
            <tr>
              <th>Project Id</th>
              <th>Project Leader</th>
              <th>members</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {projectData.map((row,index)=>(
              <tr key={index}>
                <td>{row.project_id}</td>
                <td>{row.project_leader}</td>
                <td>{row.members}</td>
                <td><button>edit</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}

export default TeamManager
