import axios from 'axios';
import React, { useState, useEffect } from 'react';

const ListProjects = (props) => {
  const [userId, setUserId] = useState(props.userId);
  const [projectData, setProjectData] = useState(null);
  
  useEffect(() => {
    setUserId(props.userId);
  }, [props.userId]);
  
  const getTable = () => {
    console.log('hi');
    axios.post('http://127.0.0.1:5000/getProject', {userId})
      .then((response) => {
        console.log(response);
        setProjectData(JSON.parse(response.data));
      })
      .catch((error) => {
        console.error(error);
      });
  }

  useEffect(() => {
    getTable();
  }, [userId]);  

  return (
    <div>
      <p>This is from ListProjects file: {userId}</p>
      {projectData && (
        <table>
          <thead>
            <tr>
              <th>Project_id</th>
              <th>Client_id</th>
              <th>Project_name</th>
              <th>Project_log</th>
              <th>domain</th>
              <th>project_leader</th>
            </tr>
          </thead>
          <tbody>
            {projectData.map((row, index) => (
              <tr key={index}>
                <td>{row.Project_id}</td>
                <td>{row.Client_id}</td>
                <td>{row.Project_name}</td>
                <td>{row.Project_log}</td>
                <td>{row.domain}</td>
                <td>{row.project_leader}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ListProjects;
