import React, { useState } from 'react'
import axios from 'axios';
import PostForm from './PostForm';
{/*ask for company name name, email, password*/}
function Signup() {
    const [data,SetData]=useState({
        userName:'',
        company:'',
        password:'',
        email:''
    });
    const changeHandler = (e) => {
        SetData({ [e.target.name]: e.target.value });
      };
    const submitHandler=(e)=>{
        e.preventDefault();
        console.log(data);
        const stateJson = JSON.stringify(this.state);
        axios.post('http://127.0.0.1:5000/signup',JSON.parse(stateJson)).then((response)=>{
            console.log(response.data);

        }).catch((error)=>{
            console.log(error);
        })
    }
    const toPostForm=(e)=>{
        e.preventDefault();
        <PostForm />
    }
  return (
    <div>
      <from onSubmit={submitHandler} action='POSTS'>
          <div>
            <p>Enter name</p>
            <input type='text' name='userName' value={userName} onChange={changeHandler} />
          </div>
          <div>
            <p>company name</p>
            <input type='text' name='company' value={company} onChange={changeHandler} />
          </div>
          <div>
            <p>Email</p>
            <input type='text' name='email' value={email} onChange={changeHandler} />
          </div>
          <div>
            <p>Enter password</p>
            <input type='text' name='password' value={password} onChange={changeHandler} />
          </div>
          <div>
            <button type='submit'>Submit</button>
          </div>
      </from>
      <div>
        <button onClick={toPostForm}>Signin</button>
      </div>
    </div>
  )
}

export default Signup
